--
-- TOC entry 606 (class 1255 OID 1204409)
-- Name: search_customers_fn(text, integer, integer); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.search_customers_fn(search_query text, page_number integer DEFAULT 1, page_size integer DEFAULT 50) RETURNS TABLE(id uuid, name text, phone text, email text, gender text, city text, country text, color_tag text, loyalty_level text, points integer, total_spent numeric, last_visit timestamp with time zone, is_active boolean, referral_source text, birth_month text, birth_day text, birthday date, initial_notes text, notes text, customer_tag text, location_description text, national_id text, joined_date date, created_at timestamp with time zone, updated_at timestamp with time zone, branch_id uuid, is_shared boolean, created_by_branch_id uuid, created_by_branch_name text, profile_image text, whatsapp text, whatsapp_opt_out boolean, referred_by uuid, created_by uuid, last_purchase_date timestamp with time zone, total_purchases integer, total_calls integer, total_call_duration_minutes numeric, incoming_calls integer, outgoing_calls integer, missed_calls integer, avg_call_duration_minutes numeric, first_call_date timestamp with time zone, last_call_date timestamp with time zone, call_loyalty_level text, total_returns integer, total_count bigint)
    LANGUAGE plpgsql STABLE
    AS $$
      DECLARE
        offset_val INTEGER;
        total_count_val BIGINT;
      BEGIN
        offset_val := (page_number - 1) * page_size;

        SELECT COUNT(*) INTO total_count_val
        FROM customers c
        WHERE
          c.name ILIKE '%' || search_query || '%'
          OR c.phone ILIKE '%' || search_query || '%'
          OR COALESCE(c.email, '') ILIKE '%' || search_query || '%'
          OR COALESCE(c.customer_tag, '') ILIKE '%' || search_query || '%';

        RETURN QUERY
        SELECT
          c.id,
          c.name,
          c.phone,
          c.email,
          COALESCE(c.gender, 'other') as gender,
          COALESCE(c.city, '') as city,
          COALESCE(c.country, '') as country,
          COALESCE(c.color_tag, 'new') as color_tag,
          COALESCE(c.loyalty_level, 'bronze') as loyalty_level,
          COALESCE(c.points, 0) as points,
          COALESCE(c.total_spent, 0) as total_spent,
          COALESCE(c.last_visit, c.created_at) as last_visit,
          COALESCE(c.is_active, true) as is_active,
          c.referral_source,
          c.birth_month,
          c.birth_day,
          c.birthday,
          c.initial_notes,
          c.notes,
          c.customer_tag,
          c.location_description,
          c.national_id,
          c.joined_date,
          c.created_at,
          c.updated_at,
          c.branch_id,
          COALESCE(c.is_shared, false) as is_shared,
          c.created_by_branch_id,
          c.created_by_branch_name,
          c.profile_image,
          COALESCE(c.whatsapp, c.phone) as whatsapp,
          COALESCE(c.whatsapp_opt_out, false) as whatsapp_opt_out,
          c.referred_by,
          c.created_by,
          c.last_purchase_date,
          COALESCE(c.total_purchases, 0) as total_purchases,
          COALESCE(c.total_calls, 0) as total_calls,
          COALESCE(c.total_call_duration_minutes, 0) as total_call_duration_minutes,
          COALESCE(c.incoming_calls, 0) as incoming_calls,
          COALESCE(c.outgoing_calls, 0) as outgoing_calls,
          COALESCE(c.missed_calls, 0) as missed_calls,
          COALESCE(c.avg_call_duration_minutes, 0) as avg_call_duration_minutes,
          c.first_call_date,
          c.last_call_date,
          COALESCE(c.call_loyalty_level, 'Basic') as call_loyalty_level,
          COALESCE(c.total_returns, 0) as total_returns,
          total_count_val as total_count
        FROM customers c
        WHERE
          c.name ILIKE '%' || search_query || '%'
          OR c.phone ILIKE '%' || search_query || '%'
          OR COALESCE(c.email, '') ILIKE '%' || search_query || '%'
          OR COALESCE(c.customer_tag, '') ILIKE '%' || search_query || '%'
        ORDER BY c.created_at DESC
        LIMIT page_size
        OFFSET offset_val;
      END;
      $$;


